import React, { useEffect, useState } from 'react';
import Form from 'react-bootstrap/Form';
import { Button, Container } from 'react-bootstrap';
import styles from './styles.module.scss';
import { useQuery } from '@tanstack/react-query';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core/hooks';
import { useNavigate } from 'react-router-dom';

function Sell() {
  const [tokenForSwap, setTokenForSwap] = useState(0);
  const { address } = useGetAccountInfo();

  const { data, isLoading } = useQuery(['balance'], () =>
    fetch(
      'http://localhost:1212/user/balance/erd1q07w9xm8avd7kwj3cgn3xrnhzg5da7e3vg7dv6gs3npyql0jpq9ss35a20/ESTAR-afaaf0'
    ).then((res) => res.json())
  );

  const handleCheck = () => (Boolean(address) ? null : useNavigate('/unlock'));

  useEffect(handleCheck, []);

  console.log('Component rerender');
  if (isLoading) return 'Loading...';
  return (
    <>
      <h1 className={styles.title}>Swap Token</h1>

      <Container className={styles.inputs}>
        <Form>
          <Form.Group className='mb-3'>
            <Form.Label className={styles.label}>Send:</Form.Label>
            <Form.Control
              placeholder='Moneda1'
              type='number'
              onChange={(e) => setTokenForSwap(Number(e.target.value))}
            />
            <Form.Label className={styles.label}>
              {data.data ? data.data : 0} ESTAR available
            </Form.Label>
          </Form.Group>
          <Form.Group className='mb-3'>
            <Form.Label className={styles.label}>Receive:</Form.Label>
            <Form.Control
              placeholder='Moneda2'
              value={tokenForSwap}
              type='number'
              disabled
            />
            <Form.Label className={styles.label}>Available for swap</Form.Label>
          </Form.Group>
          <Button className={styles.buttonSell} type='submit'>
            Swap
          </Button>
        </Form>
      </Container>
    </>
  );
}

export default Sell;
